import axios from "axios";
import moment from "moment";
import ReactDOM from 'react-dom';
import { useEffect, useState } from "react";
import InfiniteScroll from "react-infinite-scroll-component";
// import Pagination from "next-pagination";
import ReactPaginate from "react-paginate";
// import { getCookie, deleteCookie } from 'cookies-next'
import { toast, ToastContainer } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

import { useNavigate } from "react-router-dom";

const UserList = () => {
    // if (localStorage.getItem('token') == undefined || localStorage.getItem('token') == undefined || localStorage.getItem('token') == '') {
    //     console.log('err')
    //     alert('Session token Expired ? Please login Again')
    //     return () => push('/auth/signIn')
    // }
    const navigate = useNavigate();
    function button(data) {
        localStorage.setItem('name', (data.name));
        localStorage.setItem('email', (data.email));
        navigate('/Profile');
    }
    //logout
    const logout = async () => {
        try {
            const res = await fetch('http://localhost:3005/api/v1/admin/auth/logout', {
                headers: {
                    Authorization: localStorage.getItem('token')
                }
            })
            const data = await res.json();
            if (data.code == 200) {
                // deleteCookie('token')
                toast.error("Session Token Expired", {
                    position: toast.POSITION.TOP_LEFT
                });
                //   push('/auth/signIn')
            }
        } catch (err) {
            console.log(err)
        }
    }

    const [product, setProduct] = useState([]);
    const [totalCount, setTotalCount] = useState('');
    const [page12, setPage12] = useState(0);
    const [searchPage, setsearchPage] = useState(0);
    const [use_effect, setUse_effect] = useState(true);
    const [search, setSearch] = useState('');
    console.log("page12", page12);
    console.log("totalCount", totalCount);
    var perPage = 16
    const fetchUser = async (newPage) => {
        try {
            const search = document.getElementById('search').value
            if (search) {
                var page = searchPage + 1
                if (totalCount / perPage > searchPage) {
                    setsearchPage(page);
                }
            } else {
                var page = page12 + 1
                setPage12(page)
            }
            const res = await fetch(`http://localhost:3005/api/v1/admin/user/list?status=true&page=${page}&pageSize=${perPage}&search=${search}`, {
                headers: {
                    Authorization: localStorage.getItem('token')
                }
            });
            const data = await res.json();
            if (data.code == 200) {
                if (page > 1) {
                    var data1 = [...product, ...data.data.items]
                } else {
                    var data1 = data.data.items
                }
                setProduct(data1);
                setTotalCount(data.data.totalCount);
            } else if (data.code == 401) {
                alert("Session token Expired ? Please login again");
                navigate('/')
            } else {
                alert(data.message)
            }
        } catch (err) {
            console.log(err)
        }
    }
    useEffect(() => {
        fetchUser();
    }, []);
    const handleOnClickFunction = () => {
        fetchUser(1)
    }
    return (
        <>
            <section>
                <div>
                    <button onClick={() => logout()} id="button">Logout</button>
                </div>
                <div class="search d-flex align-items-center w-100">
                    <div class="search_data">
                        <input type="text" class="form-control" placeholder="Search Something" id="search" name="search" />
                    </div>
                    <button class="btn" id="btnId" onClick={() => handleOnClickFunction()} > <img
                        src="/Auth-Image/icons8-search-50.png" /></button>
                </div>
                {product.length > 0 &&
                    <InfiniteScroll
                        dataLength={product?.length}
                        next={() => fetchUser()}
                        hasMore={totalCount / perPage > page12 ? true : false}
                    >
                        <table class="table">
                            <thead class="thead-dark">
                                <tr>
                                    <th scope="col">#</th>
                                    <th scope="col">Name</th>
                                    <th scope="col">Email</th>
                                    <th scope="col">Status</th>
                                    <th scope="col">Created</th>
                                    <th scope="col">State</th>
                                    <th scope="col">City</th>
                                </tr>
                            </thead>
                            <tbody>
                                {product.map((data, index) => {
                                    return (
                                        <tr>
                                            <th scope="row">{index + 1}</th>
                                            <td onClick={() => button(data)}><h3 style={{ color: 'blue' }}>{data.name}</h3></td>
                                            <td>{data.email ? data.email : "email"}</td>
                                            <td>{`${data.isActive}`}</td>
                                            <td>{moment(data.createdAt).format('DD MMM YYYY, HH:mm')}</td>
                                            <td>{data.userAddress.length ? data.userAddress[0].state : "state"}</td>
                                            <td>{data.userAddress.length ? data.userAddress[0].city : "city"}</td>
                                        </tr>
                                    )
                                })}
                            </tbody>
                        </table>
                    </InfiniteScroll>
                }
                <ToastContainer />

            </section>
        </>
    )
}
export default UserList
