import axios from "axios";
import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";

// import { NextRequest,NextResponse } from "next/server";
// import Cookies from 'cookies'
// import cookieCutter from 'cookie-cutter'
// import { getCookie } from 'cookies-next'
import { toast, ToastContainer } from 'react-toastify';

const Profile = () => {

    const navigate = useNavigate();
    const [name, setName] = useState("");
    const [email, setEmail] = useState("");
    const [nameErr, setNameErr] = useState("")
    useEffect(() => {
        axios.get('http://localhost:3005/api/v1/admin/profile/details',
            {
                headers: {
                    Authorization: localStorage.getItem('token') //the token is a variable which holds the token
                }
            }
        ).then(function (response) {
            setName(response.data.data.name ? response.data.data.name : "")
            setEmail(response.data.data.email)
        }).catch(function (error) {
            console.log(error);
        });
    }, []);

    const handleInputChange = (e) => {
        const { id, value } = e.target;
        if (id === "name") {
            setNameErr(false)
            setName(value);

        } if (id === "email") {
            setEmail(value);
            console.log(email)
        }
    }

    const handleSubmit = async (e) => {
        e.preventDefault()
        try {
            // const { data } = await axios({
            //     method: 'put',
            //     url: 'http://localhost:3005/api/v1/admin/profile/updateProfile',
            //     headers: {
            //         Authorization: getCookie('token')

            //         //the token is a variable which holds the token
            //     },
            //     data: {
            //         email: email,
            //         name: name
            //     }
            // });
            // if (data.code == 201) {
            //     push('/user/userList');
            // }
            if (!name) {
                setNameErr(true)
            }
            const data = await toast.promise(axios({
                method: 'put',
                url: 'http://localhost:3005/api/v1/admin/profile/updateProfile',
                headers: {
                    Authorization: localStorage.getItem('token')
                },
                data: {
                    email: email,
                    name: name
                }
            }), {
                pending: "Promise is pending",
                success: "Profile updated successfully",
                error: "error"
            });
            setTimeout(push1, 3000)
            function push1() {
                if (data.data.code == 201) {
                    navigate('/userList')
                }
            }
        } catch (err) {
            console.log(err.message);
            alert(err.message)
        }
    }
    return (
        <>
            <div>
                <button type="button" class="btn btn-primary">Logout</button>
            </div>
            <form className="w-50 m-auto">
                <div class="form-group">
                    <label for="exampleInputPassword1">Name</label>
                    {nameErr ? <span className="position-absolute text-danger" style={{ top: "-25px", left: "10px" }}>Please Enter Name</span> : ""}
                    <input type="Enter Name" class="form-control" id="name" value={name.trim()} onChange={(e) => handleInputChange(e)} placeholder="Password" />
                </div>
                <div class="form-group">
                    <label for="exampleInputEmail1">Email</label>
                    <input type="email" class="form-control" id="email" value={email.trim()} onChange={(e) => handleInputChange(e)} aria-describedby="emailHelp" placeholder="Enter email" />
                    <small id="emailHelp" class="form-text text-muted">We'll never share your email with anyone else.</small>
                </div>

                <button onClick={(e) => handleSubmit(e)} type="button" class="btn btn-primary">Submit</button>
            </form>
            <ToastContainer />
        </>
    )
}
export default Profile
