import axios from "axios";
import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";

// import { NextRequest,NextResponse } from "next/server";
// import Cookies from 'cookies'
// import cookieCutter from 'cookie-cutter'
// import { getCookie } from 'cookies-next'
import { toast, ToastContainer } from 'react-toastify';

const Profile = () => {

    const navigate = useNavigate();
    const [name, setName] = useState("");
    const [email, setEmail] = useState("");
    const [nameErr, setNameErr] = useState(false)
    const [nameErrLength, setNameErrLength] = useState(false)

    useEffect(() => {
        axios.get('http://localhost:3005/api/v1/admin/profile/details',
            {
                headers: {
                    Authorization: localStorage.getItem('token') //the token is a variable which holds the token
                }
            }
        ).then(function (response) {
            setName(response.data.data.name ? response.data.data.name : "")
            setEmail(response.data.data.email)
        }).catch(function (error) {
            console.log(error);
        });
    }, []);

    const handleInputChange = (e) => {
        const { id, value } = e.target;
        if (id === "name") {
            console.log(name)
            setNameErr(false)
            if (value.length < 3) {
                setNameErrLength(true)
                setName(value);
            } else {
                setNameErrLength(false)
                setName(value);
            }

        } if (id === "email") {
            setEmail(value);
            console.log(email)
        }
    }

    const handleSubmit = async (e) => {
        e.preventDefault();
        setNameErrLength(false)

        try {
            // const { data } = await axios({
            //     method: 'put',
            //     url: 'http://localhost:3005/api/v1/admin/profile/updateProfile',
            //     headers: {
            //         Authorization: getCookie('token')

            //         //the token is a variable which holds the token
            //     },
            //     data: {
            //         email: email,
            //         name: name
            //     }
            // });
            // if (data.code == 201) {
            //     push('/user/userList');
            // }
            if (!name) {
                console.log('enen')
                setNameErr(true)
            } else {

                console.log(email, "email__________________-------", name, "mame")
                const data = await toast.promise(axios({
                    method: 'put',
                    url: 'http://localhost:3005/api/v1/admin/profile/updateProfile',
                    headers: {
                        Authorization: localStorage.getItem('token')
                    },
                    data: {
                        email: email,
                        name: name
                    }
                }), {
                    pending: "Promise is pending",
                    success: "Profile updated successfully",
                    error: "error"
                });
                setTimeout(push1, 3000)
                function push1() {
                    if (data.data.code == 201) {
                        navigate('/userList')
                    }
                }
            }

        } catch (err) {
            console.log(err.message);
            alert(err.message)
        }
    }
    return (
        <>
            <div class="row">
                {/* <div class="col-lg-6">
                    <div class="loginPageiImg_Container">
                        <img src="/Auth_Image/signupimage2.png" />
                    </div>
                </div> */}
                <div class="col-lg-6">
                    <div class="loginscreen animated fadeInDown loginPage">
                        <div class="form-group position-relative">
                            {/* {emailErr ? <span className="position-absolute text-danger" style={{ top: "-25px", left: "10px" }}>Please Enter Valid Email</span> : ""}
                            {emailErr1 ? <span className="position-absolute text-danger" style={{ top: "-25px", left: "10px" }}>Please Enter Email</span> : ""} */}

                            <input type="email" class="form-control" placeholder="Enter Email" id="email" name="email" value={email.trim()} onChange={(e) => handleInputChange(e)} />
                        </div>
                        <div class="form-group position-relative">
                            {nameErrLength ? <span className="position-absolute text-danger" style={{ top: "-25px", left: "10px" }}>Name length must be at least 3 characters long" </span> : ""}
                            {nameErr ? <span className="position-absolute text-danger" style={{ top: "-25px", left: "10px" }}>Please Enter Name</span> : ""}
                            <input type="name" class="form-control" placeholder="Enter Name" id="name" name="name" value={name} onChange={(e) => handleInputChange(e)} />
                        </div>
                        <button class="btn block full-width m-b" id="btnId" onClick={(e) => handleSubmit(e)} >
                            Submit
                        </button>
                    </div>
                </div>
            </div>
            <ToastContainer />
        </>
    )
}
export default Profile
